mapper.select:
select userNo, userId, userPwd, userName, userEmail,
userAddr, userTel, userGender, to_char(userBirth, 'YYYY-MM-DD') userBirth 
from users where userId=#{userId}

mapper.update:
update users set
userId=#{userId, jdbcType=VARCHAR}, userPwd=#{userPwd, jdbcType=VARCHAR}, userName=#{userName, jdbcType=VARCHAR}, userEmail=#{userEmail, jdbcType=VARCHAR}, userAddr=#{userAddr, jdbcType=VARCHAR}, userTel=#{userTel, jdbcType=VARCHAR}, userBirth=#{userBirth, jdbcType=VARCHAR}, userGender=#{userGender, jdbcType=VARCHAR}
where userId=#{userId}

update users set userId=#{userId, jdbcType=VARCHAR},
userPwd=#{userPwd, jdbcType=VARCHAR},
userName=#{userName, jdbcType=VARCHAR}, userEmail=#{userEmail, jdbcType=VARCHAR},
userAddr=#{userAddr, jdbcType=VARCHAR}, userTel=#{userTel, jdbcType=VARCHAR},
userGender=#{userGender, jdbcType=VARCHAR}
where userNo=#{userNo}

update users set userId=#{userId, jdbcType=VARCHAR},
userPwd=#{userPwd, jdbcType=VARCHAR},
userName=#{userName, jdbcType=VARCHAR}, userEmail=#{userEmail, jdbcType=VARCHAR},
userAddr=#{userAddr, jdbcType=VARCHAR}, userTel=#{userTel, jdbcType=VARCHAR},
userGender=#{userGender, jdbcType=VARCHAR}
where userNo=#{userNo}
